class ArticalModel {
  String? status;
  int? totalResults;
  List<Articles>? articles;

  ArticalModel({
    this.status,
    this.totalResults,
    this.articles,
  });

  ArticalModel.fromJson(dynamic json) {
    status = json['status'];
    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      articles = [];
      json['articles'].forEach((v) {
        articles?.add(Articles.fromJson(v));
      });
    }
  }
}

